//---------------------------------------------

function rango_azar(maximum) {
  return Math.floor(Math.random() * (maximum + 1));
}

function elije(array) {
  return array[rango_azar(array.length - 1)];
}

function posible(palabras) {
  if (Math.random() > 0.5) {
    return ' ' + elije(palabras);
  }
  return '';
}

function largo() {
  return ' MI' + posible(adjetivos) + ' ' + elije(sustantivos) + posible(adverbios) + ' ' + elije(verbos) + ' TU' + posible(adjetivos) + ' ' + elije(sustantivos) + '.';

}

function corto() {
  return ' ' + elije(adjetivos) + ' ' + elije(sustantivos) + '.';

}

function carta() {
  
  var tu_eres = false;

  texto = texto + elije(primero) + ' ' + elije(segundo) + '\n\n';

  for (var i = 0; i < 5; i = i + 1) {
   var tipo_enunciado = elije(['largo', 'corto']);
    if (tipo_enunciado === 'largo') {
      texto = texto + largo();
      tu_eres = false;

    } else {
      if (tu_eres) {
        texto = texto.slice(0, -1) + ': MI' + corto();
        tu_eres = false;
      } else {
        texto = texto + 'TU ERES MI' + corto();
        tu_eres = true;
      }
    }
  }
  
  texto = texto + '\n\nDE TU ' + elije(adverbios) + ' PERTENENCIA' + '\nM.U.C.\n\n';

  return texto;
}



